document.addEventListener("DOMContentLoaded", function() {
  var menuButton = document.querySelector('.dl-trigger');
  var menu = document.querySelector('.dl-menu');

  // Function to toggle the menu
  function toggleMenu() {
      menu.classList.toggle('dl-menuopen');
  }

  // Event listener for the menu button click
  menuButton.addEventListener('click', function(e) {
      e.preventDefault();
      toggleMenu();
  });

  // Event listener to close the menu when clicking outside of it
  document.addEventListener('click', function(e) {
      if (!menu.contains(e.target) && !menuButton.contains(e.target)) {
          menu.classList.remove('dl-menuopen');
      }
  });

  // Prevent closing the menu when clicking inside it
  menu.addEventListener('click', function(e) {
      e.stopPropagation();
  });
});
